// ✅ Updated React Dashboard Code with Enhanced UI
import React, { useState } from "react";
import {
  BarChart, Bar, ResponsiveContainer, Tooltip, XAxis, YAxis, CartesianGrid, Legend
} from "recharts";
import { FaUpload, FaFilter, FaChartBar, FaInfoCircle } from "react-icons/fa";

const COLORS = ["#2563eb", "#f97316", "#10b981", "#ef4444", "#6366f1", "#eab308", "#14b8a6", "#7c3aed", "#3b82f6", "#dc2626"];

const UploadCard = ({ onUpload }) => {
  const fileInputRef = React.useRef(null);

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-[400px] w-full max-w-2xl mx-auto">
      <div className="w-full flex justify-center items-center border-dashed border-4 border-blue-300 rounded-2xl bg-white/70 backdrop-blur-md shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-[1.02] p-12">
        <label className="flex flex-col items-center space-y-6 cursor-pointer">
          <div className="relative">
            <div className="absolute inset-0 bg-blue-100 rounded-full animate-ping"></div>
            <FaUpload className="text-6xl text-blue-500 relative z-10" />
          </div>
          <div className="text-center space-y-2">
            <p className="text-gray-700 font-semibold text-xl">Welcome to Mediversal Dashboard</p>
            <p className="text-gray-500">Upload your JSON file to visualize employee feedback</p>
          </div>
          <div className="flex flex-col items-center space-y-4">
            <button 
              type="button"
              onClick={handleButtonClick}
              className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-6 rounded-lg transition-all duration-200 transform hover:scale-105"
            >
              Choose File
            </button>
            <p className="text-gray-500 text-sm">or drag and drop your file here</p>
          </div>
          <input 
            ref={fileInputRef}
            type="file" 
            accept="application/JSON" 
            onChange={onUpload} 
            className="hidden" 
          />
        </label>
      </div>
    </div>
  );
};

const FilterPanel = ({ filters, setFilters, options }) => (
  <div className="flex flex-wrap gap-4 justify-center py-6 bg-white/80 backdrop-blur-sm rounded-2xl shadow-md p-4">
    <div className="flex items-center gap-2 text-blue-600">
      <FaFilter className="text-xl" />
      <span className="font-semibold">Filters</span>
    </div>
    {Object.entries(filters).map(([key, value]) => (
      <select
        key={key}
        className="border rounded-xl px-4 py-2 shadow-sm text-sm bg-white hover:bg-gray-50 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200"
        value={value}
        onChange={(e) => setFilters((prev) => ({ ...prev, [key]: e.target.value }))}
      >
        <option value="">All {key}</option>
        {options[key]?.map((opt, i) => (
          <option key={i} value={opt}>{opt}</option>
        ))}
      </select>
    ))}
  </div>
);

const Insight = ({ text }) => (
  <div className="flex items-start gap-2 text-sm text-gray-600 mt-4 p-3 bg-blue-50 rounded-lg">
    <FaInfoCircle className="text-blue-500 mt-1" />
    <p className="italic">{text}</p>
  </div>
);

const Section = ({ title, children }) => (
  <div className="rounded-2xl bg-white p-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-[1.01] border border-gray-100">
    <div className="flex items-center gap-2 mb-4">
      <FaChartBar className="text-blue-500" />
      <h2 className="text-lg font-bold text-gray-800">{title}</h2>
    </div>
    {children}
  </div>
);

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [filters, setFilters] = useState({ Department: "", Years: "", Workplace: "" });

  const handleUpload = (e) => {
    const reader = new FileReader();
    reader.onload = (evt) => {
      try {
        const parsed = JSON.parse(evt.target.result);
        setData(parsed);
      } catch {
        alert("Invalid JSON file. Please upload a valid format.");
      }
    };
    reader.readAsText(e.target.files[0]);
  };

  const filteredData = data.filter((d) =>
    (!filters.Department || d["What is your department?"] === filters.Department) &&
    (!filters.Years || d["Years of Service"] === filters.Years) &&
    (!filters.Workplace || d["What is your primary place of work?"] === filters.Workplace)
  );

  const countByResponse = (key, responses) =>
    responses.map((r) => ({ name: r, value: filteredData.filter((d) => d[key] === r).length }));

  const extractOptions = (field) => [...new Set(data.map((d) => d[field]).filter(Boolean))];

  const options = {
    Department: extractOptions("What is your department?"),
    Years: extractOptions("Years of Service"),
    Workplace: extractOptions("What is your primary place of work?")
  };

  const charts = [
    { title: "📊 Overall Satisfaction", key: "Overall, how satisfied are you with your experience working at Mediversal?", responses: ["Very Dissatisfied", "Dissatisfied", "Neutral", "Satisfied", "Very Satisfied"], insight: "Majority of employees report being satisfied or very satisfied." },
    { title: "⚖️ Work-Life Balance", key: "How satisfied are you with your work-life balance?", responses: ["Very Dissatisfied", "Dissatisfied", "Neutral", "Satisfied", "Very Satisfied"], insight: "Work-life balance is generally rated positively but with some scope for improvement." },
    { title: "👥 HR Support", key: "Do you feel that HR adequately supports the employees in resolving concerns and improving their work experience?", responses: ["Not supported at all", "Rarely supported", "Somewhat supported", "Mostly supported", "Completely supported"], insight: "Most employees feel supported by HR, though some express concerns." },
    { title: "🧩 Discrimination Perception", key: "Do you feel there is any discrimination in the work environment based on caste, religion, gender, or region?", responses: ["Never", "Rarely", "Sometimes", "Often"], insight: "Majority report no discrimination, with minor exceptions." },
    { title: "📈 Management Confidence", key: "Do you have confidence in the management's ability to lead and address concerns within the organization?", responses: ["Not confident", "Neutral", "Mostly confident", "Completely confident"], insight: "Leadership trust is generally strong." },
    { title: "💰 Salary Satisfaction", key: "Do you believe that the salary you receive is in line with the market standard for your role and experience?", responses: ["Not at all", "Slightly", "Mostly", "Completely"], insight: "Salary alignment is a recurring concern." },
    { title: "🔁 Retention Intent", key: "Overall, do you see yourself staying long-term at Mediversal?", responses: ["Unlikely", "Neutral", "Likely", "Definitely"], insight: "High long-term commitment across employees." },
    { title: "🏥 Recommend Mediversal", key: "Would you recommend Mediversal to others looking for a job e.g., candidates or peers?", responses: ["Definitely", "Likely", "Neutral", "Unlikely"], insight: "Mediversal is widely recommended as an employer." },
    { title: "🤝 Integration in Organization", key: "Do you feel integrated within the organization as a whole, or only with the unit you are posted in?", responses: ["Not integrated at all", "Only with my unit", "Mostly integrated", "Fully integrated across the organization"], insight: "Organizational integration is mostly positive but not universal." },
    { title: "🛡️ Workplace Safety", key: "Do you feel safe within your workplace environment?", responses: ["Unsafe", "Neutral", "Safe", "Very safe"], insight: "Workplace safety sentiment is overwhelmingly positive." },
    { title: "👨‍⚕️ Recommend for Admission", key: "Would you recommend your friends or family members to admit their near and dear ones in Mediversal Hospital?", responses: ["Definitely", "Likely", "Neutral", "Unlikely"], insight: "Staff trust the hospital for patient care as well." },
    { title: "📣 Brand Value", key: "How would you rate the brand value of Mediversal in the healthcare market?", responses: ["Poor", "Neutral", "Good", "Excellent"], insight: "Brand reputation is rated strong." },
    { title: "⏳ Years of Service vs Salary", key: "Years of Service", groupKey: "Do you believe that the salary you receive is in line with the market standard for your role and experience?", insight: "Newer employees report more salary dissatisfaction." }
  ];

  const getGroupedData = (groupField, valueField, allValues) => {
    const groups = extractOptions(groupField);
    return groups.map((g) => {
      const entries = filteredData.filter((d) => d[groupField] === g);
      const obj = { name: g };
      allValues.forEach((val) => {
        obj[val] = entries.filter((e) => e[valueField] === val).length;
      });
      return obj;
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50 p-6 space-y-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold text-blue-800 text-center mb-2">Mediversal Employee Feedback Dashboard</h1>
        <p className="text-center text-gray-600 mb-8">Visualizing employee feedback and insights</p>

        {data.length === 0 ? (
          <UploadCard onUpload={handleUpload} />
        ) : (
          <>
            <FilterPanel filters={filters} setFilters={setFilters} options={options} />
            <div className="flex justify-center w-full my-6">
              <div className="bg-white/80 backdrop-blur-sm rounded-full py-2 px-6 shadow-md inline-flex items-center gap-2">
                <span className="text-blue-500">📊</span>
                <span className="text-gray-600 font-medium">Showing {filteredData.length} responses</span>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-8">
              {charts.map((chart, idx) => (
                <Section key={idx} title={chart.title}>
                  <ResponsiveContainer width="100%" height={360}>
                    {chart.groupKey ? (
                      <BarChart data={getGroupedData(chart.key, chart.groupKey, extractOptions(chart.groupKey))}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                        <XAxis dataKey="name" stroke="#6b7280" />
                        <YAxis allowDecimals={false} stroke="#6b7280" />
                        <Tooltip 
                          contentStyle={{ 
                            backgroundColor: 'rgba(255, 255, 255, 0.9)',
                            border: 'none',
                            borderRadius: '0.5rem',
                            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                          }}
                        />
                        <Legend />
                        {extractOptions(chart.groupKey).map((val, i) => (
                          <Bar key={val} dataKey={val} fill={COLORS[i % COLORS.length]} radius={[5, 5, 0, 0]} />
                        ))}
                      </BarChart>
                    ) : (
                      <BarChart data={countByResponse(chart.key, chart.responses).sort((a, b) => chart.responses.indexOf(a.name) - chart.responses.indexOf(b.name))}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                        <XAxis 
                          dataKey="name" 
                          interval={0} 
                          angle={-20} 
                          textAnchor="end" 
                          height={60} 
                          stroke="#6b7280"
                          tick={{ fontSize: 12, wordBreak: 'break-word' }} 
                        />
                        <YAxis allowDecimals={false} stroke="#6b7280" />
                        <Tooltip 
                          contentStyle={{ 
                            backgroundColor: 'rgba(255, 255, 255, 0.9)',
                            border: 'none',
                            borderRadius: '0.5rem',
                            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                          }}
                        />
                        <Bar 
                          dataKey="value" 
                          fill={COLORS[idx % COLORS.length]} 
                          radius={[5, 5, 0, 0]} 
                          animationDuration={1500}
                        />
                      </BarChart>
                    )}
                  </ResponsiveContainer>
                  <Insight text={chart.insight} />
                </Section>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
