// âœ… Modern Dark Theme Dashboard with Multiple Chart Types
import React, { useState } from "react";
import {
  BarChart, Bar, LineChart, Line, PieChart, Pie, Cell,
  ResponsiveContainer, Tooltip, XAxis, YAxis, CartesianGrid, Legend
} from "recharts";
import { FaUpload, FaFilter, FaChartBar, FaInfoCircle, FaUsers, FaChartLine, FaChartPie } from "react-icons/fa";

const COLORS = ["#3b82f6", "#10b981", "#f59e0b", "#ef4444", "#8b5cf6", "#ec4899", "#14b8a6", "#f97316", "#6366f1", "#06b6d4"];

const UploadCard = ({ onUpload }) => {
  const fileInputRef = React.useRef(null);

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-[500px] w-full max-w-3xl mx-auto">
      <div className="w-full flex justify-center items-center border-2 border-gray-700 rounded-2xl bg-gray-800/90 backdrop-blur-md shadow-2xl hover:shadow-blue-500/20 transition-all duration-300 transform hover:scale-[1.01] p-12">
        <label className="flex flex-col items-center space-y-8 cursor-pointer">
          <div className="relative">
            <div className="absolute inset-0 bg-blue-500/20 rounded-full animate-ping"></div>
            <div className="relative z-10 bg-gradient-to-br from-blue-500 to-blue-600 p-6 rounded-full">
              <FaUpload className="text-4xl text-white" />
            </div>
          </div>
          <div className="text-center space-y-3">
            <h2 className="text-2xl font-bold text-white">Welcome to Mediversal Analytics</h2>
            <p className="text-gray-400">Upload your employee feedback data to generate insights</p>
          </div>
          <div className="flex flex-col items-center space-y-4">
            <button 
              type="button"
              onClick={handleButtonClick}
              className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white font-semibold py-3 px-8 rounded-lg transition-all duration-200 transform hover:scale-105 shadow-lg shadow-blue-500/20"
            >
              Choose File
            </button>
            <p className="text-gray-500 text-sm">or drag and drop your file here</p>
          </div>
          <input 
            ref={fileInputRef}
            type="file" 
            accept="application/JSON" 
            onChange={onUpload} 
            className="hidden" 
          />
        </label>
      </div>
    </div>
  );
};

const FilterPanel = ({ filters, setFilters, options }) => (
  <div className="flex flex-wrap gap-4 justify-center py-6 bg-gray-800/90 backdrop-blur-sm rounded-2xl shadow-lg p-6 border border-gray-700">
    <div className="flex items-center gap-2 text-blue-400">
      <FaFilter className="text-xl" />
      <span className="font-semibold">Filters</span>
    </div>
    {Object.entries(filters).map(([key, value]) => (
      <select
        key={key}
        className="border border-gray-600 rounded-xl px-4 py-2 text-sm bg-gray-700 text-gray-200 hover:bg-gray-600 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200"
        value={value}
        onChange={(e) => setFilters((prev) => ({ ...prev, [key]: e.target.value }))}
      >
        <option value="" className="bg-gray-700">All {key}</option>
        {options[key]?.map((opt, i) => (
          <option key={i} value={opt} className="bg-gray-700">{opt}</option>
        ))}
      </select>
    ))}
  </div>
);

const Insight = ({ text }) => (
  <div className="flex items-start gap-3 text-sm text-gray-300 mt-4 p-4 bg-gray-800/50 rounded-lg border border-gray-700">
    <FaInfoCircle className="text-blue-400 mt-1" />
    <p className="italic">{text}</p>
  </div>
);

const ChartTypeIcon = ({ type }) => {
  switch (type) {
    case 'bar':
      return <FaChartBar className="text-white" />;
    case 'line':
      return <FaChartLine className="text-white" />;
    case 'pie':
      return <FaChartPie className="text-white" />;
    default:
      return <FaChartBar className="text-white" />;
  }
};

const Section = ({ title, children, chartType = 'bar' }) => (
  <div className="rounded-2xl bg-gray-800/90 p-6 shadow-lg hover:shadow-blue-500/20 transition-all duration-300 transform hover:scale-[1.01] border border-gray-700">
    <div className="flex items-center gap-3 mb-4">
      <div className="bg-gradient-to-br from-blue-500 to-blue-600 p-2 rounded-lg">
        <ChartTypeIcon type={chartType} />
      </div>
      <h2 className="text-lg font-bold text-white">{title}</h2>
    </div>
    {children}
  </div>
);

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [filters, setFilters] = useState({ Department: "", Years: "", Workplace: "" });

  const handleUpload = (e) => {
    const reader = new FileReader();
    reader.onload = (evt) => {
      try {
        const parsed = JSON.parse(evt.target.result);
        setData(parsed);
      } catch {
        alert("Invalid JSON file. Please upload a valid format.");
      }
    };
    reader.readAsText(e.target.files[0]);
  };

  const filteredData = data.filter((d) =>
    (!filters.Department || d["What is your department?"] === filters.Department) &&
    (!filters.Years || d["Years of Service"] === filters.Years) &&
    (!filters.Workplace || d["What is your primary place of work?"] === filters.Workplace)
  );

  const countByResponse = (key, responses) =>
    responses.map((r) => ({ name: r, value: filteredData.filter((d) => d[key] === r).length }));

  const extractOptions = (field) => [...new Set(data.map((d) => d[field]).filter(Boolean))];

  const options = {
    Department: extractOptions("What is your department?"),
    Years: extractOptions("Years of Service"),
    Workplace: extractOptions("What is your primary place of work?")
  };

  const charts = [
    { 
      title: "ðŸ“Š Overall Satisfaction", 
      key: "Overall, how satisfied are you with your experience working at Mediversal?", 
      responses: ["Very Dissatisfied", "Dissatisfied", "Neutral", "Satisfied", "Very Satisfied"], 
      insight: "Majority of employees report being satisfied or very satisfied.",
      type: 'bar'
    },
    { 
      title: "âš–ï¸ Work-Life Balance Trend", 
      key: "How satisfied are you with your work-life balance?", 
      responses: ["Very Dissatisfied", "Dissatisfied", "Neutral", "Satisfied", "Very Satisfied"], 
      insight: "Work-life balance is generally rated positively but with some scope for improvement.",
      type: 'line'
    },
    { 
      title: "ðŸ‘¥ HR Support Distribution", 
      key: "Do you feel that HR adequately supports the employees in resolving concerns and improving their work experience?", 
      responses: ["Not supported at all", "Rarely supported", "Somewhat supported", "Mostly supported", "Completely supported"], 
      insight: "Most employees feel supported by HR, though some express concerns.",
      type: 'pie'
    },
    { 
      title: "ðŸ§© Discrimination Perception", 
      key: "Do you feel there is any discrimination in the work environment based on caste, religion, gender, or region?", 
      responses: ["Never", "Rarely", "Sometimes", "Often"], 
      insight: "Majority report no discrimination, with minor exceptions.",
      type: 'bar'
    },
    { 
      title: "ðŸ“ˆ Management Confidence Trend", 
      key: "Do you have confidence in the management's ability to lead and address concerns within the organization?", 
      responses: ["Not confident", "Neutral", "Mostly confident", "Completely confident"], 
      insight: "Leadership trust is generally strong.",
      type: 'line'
    },
    { 
      title: "ðŸ’° Salary Satisfaction", 
      key: "Do you believe that the salary you receive is in line with the market standard for your role and experience?", 
      responses: ["Not at all", "Slightly", "Mostly", "Completely"], 
      insight: "Salary alignment is a recurring concern.",
      type: 'bar'
    },
    { 
      title: "ðŸ” Retention Distribution", 
      key: "Overall, do you see yourself staying long-term at Mediversal?", 
      responses: ["Unlikely", "Neutral", "Likely", "Definitely"], 
      insight: "High long-term commitment across employees.",
      type: 'pie'
    },
    { 
      title: "ðŸ¥ Recommend Mediversal", 
      key: "Would you recommend Mediversal to others looking for a job e.g., candidates or peers?", 
      responses: ["Definitely", "Likely", "Neutral", "Unlikely"], 
      insight: "Mediversal is widely recommended as an employer.",
      type: 'bar'
    },
    { 
      title: "ðŸ¤ Integration in Organization", 
      key: "Do you feel integrated within the organization as a whole, or only with the unit you are posted in?", 
      responses: ["Not integrated at all", "Only with my unit", "Mostly integrated", "Fully integrated across the organization"], 
      insight: "Organizational integration is mostly positive but not universal.",
      type: 'bar'
    },
    { 
      title: "ðŸ›¡ï¸ Workplace Safety", 
      key: "Do you feel safe within your workplace environment?", 
      responses: ["Unsafe", "Neutral", "Safe", "Very safe"], 
      insight: "Workplace safety sentiment is overwhelmingly positive.",
      type: 'bar'
    },
    { 
      title: "ðŸ‘¨â€âš•ï¸ Recommend for Admission", 
      key: "Would you recommend your friends or family members to admit their near and dear ones in Mediversal Hospital?", 
      responses: ["Definitely", "Likely", "Neutral", "Unlikely"], 
      insight: "Staff trust the hospital for patient care as well.",
      type: 'bar'
    },
    { 
      title: "ðŸ“£ Brand Value", 
      key: "How would you rate the brand value of Mediversal in the healthcare market?", 
      responses: ["Poor", "Neutral", "Good", "Excellent"], 
      insight: "Brand reputation is rated strong.",
      type: 'bar'
    }
  ];

  const renderChart = (chart, idx) => {
    const data = countByResponse(chart.key, chart.responses).sort((a, b) => 
      chart.responses.indexOf(a.name) - chart.responses.indexOf(b.name)
    );

    switch (chart.type) {
      case 'line':
        return (
          <LineChart data={data}>
            <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
            <XAxis 
              dataKey="name" 
              stroke="#9CA3AF"
              tick={{ fontSize: 12, wordBreak: 'break-word' }}
            />
            <YAxis allowDecimals={false} stroke="#9CA3AF" />
            <Tooltip 
              contentStyle={{ 
                backgroundColor: 'rgba(31, 41, 55, 0.9)',
                border: '1px solid #374151',
                borderRadius: '0.5rem',
                color: '#F3F4F6'
              }}
            />
            <Line 
              type="monotone" 
              dataKey="value" 
              stroke={COLORS[idx % COLORS.length]} 
              strokeWidth={2}
              dot={{ fill: COLORS[idx % COLORS.length], strokeWidth: 2 }}
              activeDot={{ r: 8 }}
            />
          </LineChart>
        );
      case 'pie':
        return (
          <PieChart>
            <Pie
              data={data}
              cx="50%"
              cy="50%"
              labelLine={false}
              label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
              outerRadius={120}
              fill="#8884d8"
              dataKey="value"
            >
              {data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip 
              contentStyle={{ 
                backgroundColor: 'rgba(31, 41, 55, 0.9)',
                border: '1px solid #374151',
                borderRadius: '0.5rem',
                color: '#F3F4F6'
              }}
            />
            <Legend />
          </PieChart>
        );
      default:
        return (
          <BarChart data={data}>
            <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
            <XAxis 
              dataKey="name" 
              interval={0} 
              angle={-20} 
              textAnchor="end" 
              height={60} 
              stroke="#9CA3AF"
              tick={{ fontSize: 12, wordBreak: 'break-word' }} 
            />
            <YAxis allowDecimals={false} stroke="#9CA3AF" />
            <Tooltip 
              contentStyle={{ 
                backgroundColor: 'rgba(31, 41, 55, 0.9)',
                border: '1px solid #374151',
                borderRadius: '0.5rem',
                color: '#F3F4F6'
              }}
            />
            <Bar 
              dataKey="value" 
              fill={COLORS[idx % COLORS.length]} 
              radius={[5, 5, 0, 0]} 
              animationDuration={1500}
            />
          </BarChart>
        );
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 p-6 space-y-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-blue-600 mb-2">
            Mediversal Employee Analytics
          </h1>
          <p className="text-gray-400">Comprehensive feedback analysis and insights</p>
        </div>

        {data.length === 0 ? (
          <UploadCard onUpload={handleUpload} />
        ) : (
          <>
            <FilterPanel filters={filters} setFilters={setFilters} options={options} />
            <div className="flex justify-center w-full my-6">
              <div className="bg-gray-800/90 backdrop-blur-sm rounded-full py-3 px-6 shadow-lg border border-gray-700 inline-flex items-center gap-3">
                <div className="bg-gradient-to-br from-blue-500 to-blue-600 p-2 rounded-full">
                  <FaUsers className="text-white" />
                </div>
                <span className="text-gray-200 font-medium">Showing {filteredData.length} responses</span>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-8">
              {charts.map((chart, idx) => (
                <Section key={idx} title={chart.title} chartType={chart.type}>
                  <ResponsiveContainer width="100%" height={360}>
                    {renderChart(chart, idx)}
                  </ResponsiveContainer>
                  <Insight text={chart.insight} />
                </Section>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
