// âœ… Modern Light Theme Dashboard with Loading States
import React, { useState } from "react";
import {
  BarChart, Bar, ResponsiveContainer, Tooltip, XAxis, YAxis, CartesianGrid, Legend
} from "recharts";
import { FaUpload, FaFilter, FaChartBar, FaInfoCircle, FaUsers, FaSpinner } from "react-icons/fa";

const COLORS = ["#3b82f6", "#10b981", "#f59e0b", "#ef4444", "#8b5cf6", "#ec4899", "#14b8a6", "#f97316", "#6366f1", "#06b6d4"];

const LoadingSpinner = () => (
  <div className="flex justify-center items-center py-8">
    <div className="relative">
      <div className="absolute inset-0 bg-blue-100 rounded-full animate-ping"></div>
      <div className="relative z-10">
        <FaSpinner className="text-blue-500 text-4xl animate-spin" />
      </div>
    </div>
  </div>
);

const SkeletonCard = () => (
  <div className="rounded-2xl bg-white p-6 shadow-lg border border-gray-100 animate-pulse">
    <div className="flex items-center gap-3 mb-4">
      <div className="bg-gray-200 p-2 rounded-lg w-8 h-8 animate-pulse"></div>
      <div className="h-6 bg-gray-200 rounded w-32 animate-pulse"></div>
    </div>
    <div className="h-64 bg-gray-100 rounded-lg animate-pulse"></div>
    <div className="mt-4 space-y-2">
      <div className="h-4 bg-gray-200 rounded w-3/4 animate-pulse"></div>
      <div className="h-4 bg-gray-200 rounded w-1/2 animate-pulse"></div>
    </div>
  </div>
);

const UploadCard = ({ onUpload, isLoading }) => {
  const fileInputRef = React.useRef(null);

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  if (isLoading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-[500px] w-full max-w-3xl mx-auto">
        <div className="w-full flex justify-center items-center border-2 border-blue-100 rounded-2xl bg-white shadow-lg p-12">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-[500px] w-full max-w-3xl mx-auto">
      <div className="w-full flex justify-center items-center border-2 border-blue-100 rounded-2xl bg-white shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-[1.01] p-12">
        <label className="flex flex-col items-center space-y-8 cursor-pointer">
          <div className="relative">
            <div className="absolute inset-0 bg-blue-100 rounded-full animate-ping"></div>
            <div className="relative z-10 bg-blue-500 p-6 rounded-full">
              <FaUpload className="text-4xl text-white" />
            </div>
          </div>
          <div className="text-center space-y-3">
            <h2 className="text-2xl font-bold text-gray-800">Welcome to Mediversal Analytics</h2>
            <p className="text-gray-600">Upload your employee feedback data to generate insights</p>
          </div>
          <div className="flex flex-col items-center space-y-4">
            <button 
              type="button"
              onClick={handleButtonClick}
              className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 px-8 rounded-lg transition-all duration-200 transform hover:scale-105 shadow-lg shadow-blue-100"
            >
              Choose File
            </button>
            <p className="text-gray-500 text-sm">or drag and drop your file here</p>
          </div>
          <input 
            ref={fileInputRef}
            type="file" 
            accept="application/JSON" 
            onChange={onUpload} 
            className="hidden" 
          />
        </label>
      </div>
    </div>
  );
};

const FilterPanel = ({ filters, setFilters, options, isLoading }) => (
  <div className="flex flex-wrap gap-4 justify-center py-6 bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
    <div className="flex items-center gap-2 text-blue-500">
      <FaFilter className="text-xl" />
      <span className="font-semibold">Filters</span>
    </div>
    {Object.entries(filters).map(([key, value]) => (
      <div key={key} className="relative">
        <select
          className={`border border-gray-200 rounded-xl px-4 py-2 text-sm bg-white text-gray-700 hover:bg-gray-50 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 ${isLoading ? 'opacity-50 cursor-not-allowed' : ''}`}
          value={value}
          onChange={(e) => setFilters(key, e.target.value)}
          disabled={isLoading}
        >
          <option value="">All {key}</option>
          {options[key]?.map((opt, i) => (
            <option key={i} value={opt}>{opt}</option>
          ))}
        </select>
        {isLoading && (
          <div className="absolute right-2 top-1/2 transform -translate-y-1/2">
            <div className="relative">
              <div className="absolute inset-0 bg-blue-100 rounded-full animate-ping"></div>
              <div className="relative z-10">
                <FaSpinner className="text-blue-500 animate-spin" />
              </div>
            </div>
          </div>
        )}
      </div>
    ))}
  </div>
);

const Insight = ({ text }) => (
  <div className="flex items-start gap-3 text-sm text-gray-600 mt-4 p-4 bg-blue-50 rounded-lg border border-blue-100">
    <FaInfoCircle className="text-blue-500 mt-1" />
    <p className="italic">{text}</p>
  </div>
);

const Section = ({ title, children, question }) => (
  <div className="rounded-2xl bg-white p-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-[1.01] border border-gray-100">
    <div className="flex items-center gap-3 mb-4 group relative">
      <div className="bg-blue-500 p-2 rounded-lg">
        <FaChartBar className="text-white" />
      </div>
      <h2 className="text-lg font-bold text-gray-800 cursor-help group-hover:text-blue-600 transition-colors duration-200">
        {title}
      </h2>
      <div className="absolute left-0 top-full mt-2 w-64 p-3 bg-white rounded-lg shadow-lg border border-gray-200 opacity-0 group-hover:opacity-100 transition-opacity duration-200 z-10">
        <p className="text-sm text-gray-600">{question}</p>
      </div>
    </div>
    {children}
  </div>
);

const CustomTooltip = ({ active, payload, label, total }) => {
  if (active && payload && payload.length) {
    const value = payload[0].value;
    const percentage = ((value / total) * 100).toFixed(1);
    return (
      <div className="bg-white p-3 rounded-lg shadow-lg border border-gray-200">
        <p className="font-semibold text-gray-800">{label}</p>
        <p className="text-blue-600 font-medium">{value} responses</p>
        <p className="text-gray-600">{percentage}% of total</p>
      </div>
    );
  }
  return null;
};

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [filters, setFilters] = useState({ Department: "", Years: "", Workplace: "" });
  const [isLoading, setIsLoading] = useState(false);
  const [isFiltering, setIsFiltering] = useState(false);

  const handleUpload = (e) => {
    setIsLoading(true);
    const reader = new FileReader();
    reader.onload = (evt) => {
      try {
        const parsed = JSON.parse(evt.target.result);
        // Increase delay to 3 seconds
        setTimeout(() => {
          setData(parsed);
          setIsLoading(false);
        }, 3000); // 3 second delay
      } catch {
        alert("Invalid JSON file. Please upload a valid format.");
        setIsLoading(false);
      }
    };
    reader.readAsText(e.target.files[0]);
  };

  const handleFilterChange = (key, value) => {
    setIsFiltering(true);
    setFilters(prev => ({ ...prev, [key]: value }));
    // Increase delay to 1.5 seconds
    setTimeout(() => {
      setIsFiltering(false);
    }, 1500); // 1.5 second delay
  };

  const filteredData = data.filter((d) =>
    (!filters.Department || d["What is your department?"] === filters.Department) &&
    (!filters.Years || d["Years of Service"] === filters.Years) &&
    (!filters.Workplace || d["What is your primary place of work?"] === filters.Workplace)
  );

  const countByResponse = (key, responses) =>
    responses.map((r) => ({ name: r, value: filteredData.filter((d) => d[key] === r).length }));

  const extractOptions = (field) => [...new Set(data.map((d) => d[field]).filter(Boolean))];

  const filterOptions = {
    Department: extractOptions("What is your department?"),
    Years: extractOptions("Years of Service"),
    Workplace: extractOptions("What is your primary place of work?")
  };

  const charts = [
    { title: "ðŸ“Š Overall Satisfaction", key: "Overall, how satisfied are you with your experience working at Mediversal?", responses: ["Very Dissatisfied", "Dissatisfied", "Neutral", "Satisfied", "Very Satisfied"], insight: "Majority of employees report being satisfied or very satisfied." },
    { title: "âš–ï¸ Work-Life Balance", key: "How satisfied are you with your work-life balance?", responses: ["Very Dissatisfied", "Dissatisfied", "Neutral", "Satisfied", "Very Satisfied"], insight: "Work-life balance is generally rated positively but with some scope for improvement." },
    { title: "ðŸ‘¥ HR Support", key: "Do you feel that HR adequately supports the employees in resolving concerns and improving their work experience?", responses: ["Not supported at all", "Rarely supported", "Somewhat supported", "Mostly supported", "Completely supported"], insight: "Most employees feel supported by HR, though some express concerns." },
    { title: "ðŸ§© Discrimination Perception", key: "Do you feel there is any discrimination in the work environment based on caste, religion, gender, or region?", responses: ["Never", "Rarely", "Sometimes", "Often"], insight: "Majority report no discrimination, with minor exceptions." },
    { title: "ðŸ“ˆ Management Confidence", key: "Do you have confidence in the management's ability to lead and address concerns within the organization?", responses: ["Not confident", "Neutral", "Mostly confident", "Completely confident"], insight: "Leadership trust is generally strong." },
    { title: "ðŸ’° Salary Satisfaction", key: "Do you believe that the salary you receive is in line with the market standard for your role and experience?", responses: ["Not at all", "Slightly", "Mostly", "Completely"], insight: "Salary alignment is a recurring concern." },
    { title: "ðŸ” Retention Intent", key: "Overall, do you see yourself staying long-term at Mediversal?", responses: ["Unlikely", "Neutral", "Likely", "Definitely"], insight: "High long-term commitment across employees." },
    { title: "ðŸ¥ Recommend Mediversal", key: "Would you recommend Mediversal to others looking for a job e.g., candidates or peers?", responses: ["Definitely", "Likely", "Neutral", "Unlikely"], insight: "Mediversal is widely recommended as an employer." },
    { title: "ðŸ¤ Integration in Organization", key: "Do you feel integrated within the organization as a whole, or only with the unit you are posted in?", responses: ["Not integrated at all", "Only with my unit", "Mostly integrated", "Fully integrated across the organization"], insight: "Organizational integration is mostly positive but not universal." },
    { title: "ðŸ›¡ï¸ Workplace Safety", key: "Do you feel safe within your workplace environment?", responses: ["Unsafe", "Neutral", "Safe", "Very safe"], insight: "Workplace safety sentiment is overwhelmingly positive." },
    { title: "ðŸ‘¨â€âš•ï¸ Recommend for Admission", key: "Would you recommend your friends or family members to admit their near and dear ones in Mediversal Hospital?", responses: ["Definitely", "Likely", "Neutral", "Unlikely"], insight: "Staff trust the hospital for patient care as well." },
    { title: "ðŸ“£ Brand Value", key: "How would you rate the brand value of Mediversal in the healthcare market?", responses: ["Poor", "Neutral", "Good", "Excellent"], insight: "Brand reputation is rated strong." }
  ];

  const getGroupedData = (groupField, valueField, allValues) => {
    const groups = extractOptions(groupField);
    return groups.map((g) => {
      const entries = filteredData.filter((d) => d[groupField] === g);
      const obj = { name: g };
      allValues.forEach((val) => {
        obj[val] = entries.filter((e) => e[valueField] === val).length;
      });
      return obj;
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50 p-6 space-y-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-blue-600 mb-2">
            Mediversal Employee Analytics
          </h1>
          <p className="text-gray-600">Comprehensive feedback analysis and insights</p>
        </div>

        {data.length === 0 ? (
          <UploadCard onUpload={handleUpload} isLoading={isLoading} />
        ) : (
          <>
            <FilterPanel 
              filters={filters} 
              setFilters={handleFilterChange} 
              options={filterOptions}
              isLoading={isFiltering}
            />
            <div className="flex justify-center w-full my-6">
              <div className="bg-white rounded-full py-3 px-6 shadow-lg border border-gray-100 inline-flex items-center gap-3">
                <div className="bg-blue-500 p-2 rounded-full">
                  <FaUsers className="text-white" />
                </div>
                <span className="text-gray-700 font-medium">Showing {filteredData.length} responses</span>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-8">
              {isLoading ? (
                // Show skeleton loading state
                Array(6).fill(0).map((_, index) => (
                  <SkeletonCard key={index} />
                ))
              ) : (
                charts.map((chart, idx) => (
                  <Section 
                    key={idx} 
                    title={chart.title}
                    question={chart.key}
                  >
                    <ResponsiveContainer width="100%" height={360}>
                      {chart.groupKey ? (
                        <BarChart data={getGroupedData(chart.key, chart.groupKey, extractOptions(chart.groupKey))}>
                          <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                          <XAxis dataKey="name" stroke="#6b7280" />
                          <YAxis allowDecimals={false} stroke="#6b7280" />
                          <Tooltip content={<CustomTooltip total={filteredData.length} />} />
                          <Legend />
                          {extractOptions(chart.groupKey).map((val, i) => (
                            <Bar key={val} dataKey={val} fill={COLORS[i % COLORS.length]} radius={[5, 5, 0, 0]} />
                          ))}
                        </BarChart>
                      ) : (
                        <BarChart data={countByResponse(chart.key, chart.responses).sort((a, b) => chart.responses.indexOf(a.name) - chart.responses.indexOf(b.name))}>
                          <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                          <XAxis 
                            dataKey="name" 
                            interval={0} 
                            angle={-20} 
                            textAnchor="end" 
                            height={60} 
                            stroke="#6b7280"
                            tick={{ fontSize: 12, wordBreak: 'break-word' }} 
                          />
                          <YAxis allowDecimals={false} stroke="#6b7280" />
                          <Tooltip content={<CustomTooltip total={filteredData.length} />} />
                          <Bar 
                            dataKey="value" 
                            fill={COLORS[idx % COLORS.length]} 
                            radius={[5, 5, 0, 0]} 
                            animationDuration={1500}
                          />
                        </BarChart>
                      )}
                    </ResponsiveContainer>
                    <Insight text={chart.insight} />
                  </Section>
                ))
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
