// âœ… Enhanced Dashboard with Theme Toggle and Improved UI
import React, { useState, useEffect } from "react";
import {
  BarChart, Bar, ResponsiveContainer, Tooltip, XAxis, YAxis, CartesianGrid, Legend
} from "recharts";
import { FaUpload, FaFilter, FaChartBar, FaInfoCircle, FaUsers, FaSun, FaMoon } from "react-icons/fa";

const COLORS = ["#3b82f6", "#10b981", "#f59e0b", "#ef4444", "#8b5cf6", "#ec4899", "#14b8a6", "#f97316", "#6366f1", "#06b6d4"];

const ThemeToggle = ({ isDark, toggleTheme }) => (
  <button
    onClick={toggleTheme}
    className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200"
  >
    {isDark ? <FaSun className="text-yellow-400" /> : <FaMoon className="text-gray-600" />}
  </button>
);

const Header = ({ title, subtitle, isDark, toggleTheme }) => (
  <div className="flex justify-between items-center mb-8">
    <div>
      <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-blue-600 mb-2">
        {title}
      </h1>
      <p className="text-gray-400">{subtitle}</p>
    </div>
    <ThemeToggle isDark={isDark} toggleTheme={toggleTheme} />
  </div>
);

const UploadCard = ({ onUpload, isDark }) => {
  const fileInputRef = React.useRef(null);

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-[500px] w-full max-w-3xl mx-auto">
      <div className="w-full flex justify-center items-center border-2 border-gray-700 rounded-2xl bg-gray-800/90 backdrop-blur-md shadow-2xl hover:shadow-blue-500/20 transition-all duration-300 transform hover:scale-[1.01] p-12">
        <label className="flex flex-col items-center space-y-8 cursor-pointer">
          <div className="relative">
            <div className="absolute inset-0 bg-blue-500/20 rounded-full animate-ping"></div>
            <div className="relative z-10 bg-gradient-to-br from-blue-500 to-blue-600 p-6 rounded-full">
              <FaUpload className="text-4xl text-white" />
            </div>
          </div>
          <div className="text-center space-y-3">
            <h2 className="text-2xl font-bold text-white">Welcome to Mediversal Analytics</h2>
            <p className="text-gray-400">Upload your employee feedback data to generate insights</p>
          </div>
          <div className="flex flex-col items-center space-y-4">
            <button 
              type="button"
              onClick={handleButtonClick}
              className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white font-semibold py-3 px-8 rounded-lg transition-all duration-200 transform hover:scale-105 shadow-lg shadow-blue-500/20"
            >
              Choose File
            </button>
            <p className="text-gray-500 text-sm">or drag and drop your file here</p>
          </div>
          <input 
            ref={fileInputRef}
            type="file" 
            accept="application/JSON" 
            onChange={onUpload} 
            className="hidden" 
          />
        </label>
      </div>
    </div>
  );
};

const FilterPanel = ({ filters, setFilters, options, isDark }) => (
  <div className="flex flex-wrap gap-4 justify-center py-6 bg-gray-800/90 backdrop-blur-sm rounded-2xl shadow-lg p-6 border border-gray-700">
    <div className="flex items-center gap-2 text-blue-400">
      <FaFilter className="text-xl" />
      <span className="font-semibold">Filters</span>
    </div>
    {Object.entries(filters).map(([key, value]) => (
      <select
        key={key}
        className="border border-gray-600 rounded-xl px-4 py-2 text-sm bg-gray-700 text-gray-200 hover:bg-gray-600 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200"
        value={value}
        onChange={(e) => setFilters((prev) => ({ ...prev, [key]: e.target.value }))}
      >
        <option value="" className="bg-gray-700">All {key}</option>
        {options[key]?.map((opt, i) => (
          <option key={i} value={opt} className="bg-gray-700">{opt}</option>
        ))}
      </select>
    ))}
  </div>
);

const Insight = ({ text, isDark }) => (
  <div className="flex items-start gap-3 text-sm text-gray-300 mt-4 p-4 bg-gray-800/50 rounded-lg border border-gray-700">
    <FaInfoCircle className="text-blue-400 mt-1" />
    <p className="italic">{text}</p>
  </div>
);

const CustomTooltip = ({ active, payload, label, total, isDark }) => {
  if (active && payload && payload.length) {
    const value = payload[0].value;
    const percentage = ((value / total) * 100).toFixed(1);
    return (
      <div className={`p-3 rounded-lg shadow-lg border ${isDark ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'}`}>
        <p className={`font-medium ${isDark ? 'text-gray-200' : 'text-gray-800'}`}>{label}</p>
        <p className={`${isDark ? 'text-gray-300' : 'text-gray-600'}`}>
          Count: {value}
        </p>
        <p className={`${isDark ? 'text-gray-300' : 'text-gray-600'}`}>
          Percentage: {percentage}%
        </p>
      </div>
    );
  }
  return null;
};

const Section = ({ title, children, isDark }) => (
  <div className={`${isDark ? 'bg-gray-800/90' : 'bg-white/90'} rounded-2xl p-6 shadow-lg border ${isDark ? 'border-gray-700' : 'border-gray-200'} mb-8`}>
    <div className="flex items-center gap-3 mb-4">
      <div className={`p-2 rounded-lg ${isDark ? 'bg-gray-700' : 'bg-gray-100'}`}>
        <FaChartBar className={`${isDark ? 'text-blue-400' : 'text-blue-500'}`} />
      </div>
      <h2 className={`text-xl font-semibold ${isDark ? 'text-gray-200' : 'text-gray-800'}`}>{title}</h2>
    </div>
    {children}
  </div>
);

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [filters, setFilters] = useState({ Department: "", Years: "", Workplace: "" });
  const [isDark, setIsDark] = useState(true);

  useEffect(() => {
    // Check system preference for dark mode
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    setIsDark(prefersDark);
  }, []);

  const toggleTheme = () => {
    setIsDark(!isDark);
  };

  const handleUpload = (e) => {
    const reader = new FileReader();
    reader.onload = (evt) => {
      try {
        const parsed = JSON.parse(evt.target.result);
        setData(parsed);
      } catch {
        alert("Invalid JSON file. Please upload a valid format.");
      }
    };
    reader.readAsText(e.target.files[0]);
  };

  const filteredData = data.filter((d) =>
    (!filters.Department || d["What is your department?"] === filters.Department) &&
    (!filters.Years || d["Years of Service"] === filters.Years) &&
    (!filters.Workplace || d["What is your primary place of work?"] === filters.Workplace)
  );

  const countByResponse = (key, responses) =>
    responses.map((r) => ({ name: r, value: filteredData.filter((d) => d[key] === r).length }));

  const extractOptions = (field) => [...new Set(data.map((d) => d[field]).filter(Boolean))];

  const options = {
    Department: extractOptions("What is your department?"),
    Years: extractOptions("Years of Service"),
    Workplace: extractOptions("What is your primary place of work?")
  };

  const charts = [
    { title: "ðŸ“Š Overall Satisfaction", key: "Overall, how satisfied are you with your experience working at Mediversal?", responses: ["Very Dissatisfied", "Dissatisfied", "Neutral", "Satisfied", "Very Satisfied"], insight: "Majority of employees report being satisfied or very satisfied." },
    { title: "âš–ï¸ Work-Life Balance", key: "How satisfied are you with your work-life balance?", responses: ["Very Dissatisfied", "Dissatisfied", "Neutral", "Satisfied", "Very Satisfied"], insight: "Work-life balance is generally rated positively but with some scope for improvement." },
    { title: "ðŸ‘¥ HR Support", key: "Do you feel that HR adequately supports the employees in resolving concerns and improving their work experience?", responses: ["Not supported at all", "Rarely supported", "Somewhat supported", "Mostly supported", "Completely supported"], insight: "Most employees feel supported by HR, though some express concerns." },
    { title: "ðŸ§© Discrimination Perception", key: "Do you feel there is any discrimination in the work environment based on caste, religion, gender, or region?", responses: ["Never", "Rarely", "Sometimes", "Often"], insight: "Majority report no discrimination, with minor exceptions." },
    { title: "ðŸ“ˆ Management Confidence", key: "Do you have confidence in the management's ability to lead and address concerns within the organization?", responses: ["Not confident", "Neutral", "Mostly confident", "Completely confident"], insight: "Leadership trust is generally strong." },
    { title: "ðŸ’° Salary Satisfaction", key: "Do you believe that the salary you receive is in line with the market standard for your role and experience?", responses: ["Not at all", "Slightly", "Mostly", "Completely"], insight: "Salary alignment is a recurring concern." },
    { title: "ðŸ” Retention Intent", key: "Overall, do you see yourself staying long-term at Mediversal?", responses: ["Unlikely", "Neutral", "Likely", "Definitely"], insight: "High long-term commitment across employees." },
    { title: "ðŸ¥ Recommend Mediversal", key: "Would you recommend Mediversal to others looking for a job e.g., candidates or peers?", responses: ["Definitely", "Likely", "Neutral", "Unlikely"], insight: "Mediversal is widely recommended as an employer." },
    { title: "ðŸ¤ Integration in Organization", key: "Do you feel integrated within the organization as a whole, or only with the unit you are posted in?", responses: ["Not integrated at all", "Only with my unit", "Mostly integrated", "Fully integrated across the organization"], insight: "Organizational integration is mostly positive but not universal." },
    { title: "ðŸ›¡ï¸ Workplace Safety", key: "Do you feel safe within your workplace environment?", responses: ["Unsafe", "Neutral", "Safe", "Very safe"], insight: "Workplace safety sentiment is overwhelmingly positive." },
    { title: "ðŸ‘¨â€âš•ï¸ Recommend for Admission", key: "Would you recommend your friends or family members to admit their near and dear ones in Mediversal Hospital?", responses: ["Definitely", "Likely", "Neutral", "Unlikely"], insight: "Staff trust the hospital for patient care as well." },
    { title: "ðŸ“£ Brand Value", key: "How would you rate the brand value of Mediversal in the healthcare market?", responses: ["Poor", "Neutral", "Good", "Excellent"], insight: "Brand reputation is rated strong." }
  ];

  const getGroupedData = (groupField, valueField, allValues) => {
    const groups = extractOptions(groupField);
    return groups.map((g) => {
      const entries = filteredData.filter((d) => d[groupField] === g);
      const obj = { name: g };
      allValues.forEach((val) => {
        obj[val] = entries.filter((e) => e[valueField] === val).length;
      });
      return obj;
    });
  };

  const renderChart = (chart) => {
    const chartData = countByResponse(chart.key, chart.responses);
    const total = chartData.reduce((sum, item) => sum + item.value, 0);

    return (
      <Section key={chart.title} title={chart.title} isDark={isDark}>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke={isDark ? '#374151' : '#e5e7eb'} />
            <XAxis 
              dataKey="name" 
              stroke={isDark ? '#9CA3AF' : '#6B7280'}
              tick={{ fontSize: 12 }}
            />
            <YAxis 
              stroke={isDark ? '#9CA3AF' : '#6B7280'}
              tick={{ fontSize: 12 }}
            />
            <Tooltip 
              content={<CustomTooltip total={total} isDark={isDark} />}
            />
            <Bar 
              dataKey="value" 
              fill={isDark ? '#3B82F6' : '#2563EB'}
              radius={[4, 4, 0, 0]}
            />
          </BarChart>
        </ResponsiveContainer>
      </Section>
    );
  };

  return (
    <div className={`min-h-screen transition-colors duration-200 ${isDark ? 'bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900' : 'bg-gradient-to-br from-blue-50 via-white to-blue-50'} p-6 space-y-8`}>
      <div className="max-w-7xl mx-auto">
        <Header 
          title="Mediversal Employee Analytics"
          subtitle="Comprehensive feedback analysis and insights"
          isDark={isDark}
          toggleTheme={toggleTheme}
        />

        {data.length === 0 ? (
          <UploadCard onUpload={handleUpload} isDark={isDark} />
        ) : (
          <>
            <FilterPanel filters={filters} setFilters={setFilters} options={options} isDark={isDark} />
            <div className="flex justify-center w-full my-6">
              <div className={`${isDark ? 'bg-gray-800/90' : 'bg-white/90'} backdrop-blur-sm rounded-full py-3 px-6 shadow-lg border ${isDark ? 'border-gray-700' : 'border-gray-200'} inline-flex items-center gap-3`}>
                <div className="bg-gradient-to-br from-blue-500 to-blue-600 p-2 rounded-full">
                  <FaUsers className="text-white" />
                </div>
                <span className={`${isDark ? 'text-gray-200' : 'text-gray-700'} font-medium`}>
                  Showing {filteredData.length} responses
                </span>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {charts.map(renderChart)}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
